import vertex from './shaders/test/vertex.js'
import fragment from './shaders/test/fragment.js'
import { useMemo, useRef } from 'react'
import { shaderMaterial,OrbitControls } from '@react-three/drei'
import { useFrame,extend } from '@react-three/fiber';

//just use the shaderMaterial helper from the drei its so simple to use
const PlaneMaterial = shaderMaterial(
    {
        u_time:0
    },
    vertex,
    fragment
)
extend({PlaneMaterial});//so that we can use it as a component


function App(){
  const ref = useRef();

  const planeMaterial = useRef();

  useFrame((state,delta)=>{
        planeMaterial.current.u_time = state.clock.elapsedTime;
  })

  return (
    <>
    <OrbitControls />{ /** using the orbit controls */}

      <mesh ref={ref}>
        <planeGeometry args={[2, 2, 32, 32]} />
        <planeMaterial ref={ planeMaterial } />{/** Now we are using it as a tag and its so simple */}
      </mesh>
    </>
  )
}
export default App
