import vertex from './shaders/test/vertex.js'
import fragment from './shaders/test/fragment.js'
import { useMemo, useRef } from 'react'
import { useFrame } from '@react-three/fiber';

function App(){
  const ref = useRef();
  const material = useRef();
  useFrame((state,delta)=>{
    console.log(state.clock.elapsedTime)
      material.current.uniforms.u_Time = state.clock.elapsedTime;
  })

  const uniforms = useMemo(()=>({
    u_Time:{value:1.0}
  }),[])

  return (
    <>
      <mesh ref={ref}>
        <planeGeometry args={[1, 1, 32, 32]} />
        <shaderMaterial 
          ref={material}
          vertexShader={vertex} 
          fragmentShader={fragment} 
          uniforms={uniforms}
          />
      </mesh>
    </>
  )
}
export default App
