import vertex from './shaders/test/vertex.js'
import fragment from './shaders/test/fragment.js'
import { useMemo, useRef } from 'react'
import { shaderMaterial } from '@react-three/drei'
import { useFrame } from '@react-three/fiber';

const material = shaderMaterial(
    {
        u_time:0
    },
    vertex,
    fragment
)


function App(){
  const ref = useRef();

  return (
    <>
      <mesh ref={ref}>
        <planeGeometry args={[1, 1, 32, 32]} />
        <shaderMaterial 
          ref={material}
          vertexShader={vertex} 
          fragmentShader={fragment} 
          uniforms={{
            uTime:{value:0}

          }}
          />
      </mesh>
    </>
  )
}
export default App
