        <planeMaterial ref={ Material } />
      </mesh> */}

    {/** for blob */}
      <mesh ref={ref} position={[0, 0, 0]}  rotation={[-Math.PI / 2, 0, 0]} scale={1.5}>
        <icosahedronGeometry args={[2, 20]} />
        <blobMaterial ref={ Material } />
      </mesh>

    </>
  )
}
export default App
